import React from "react";
import Hijo from "../Hijo"

class Padre extends React.Component{

    state = {
        listaAmigos:["LUCAS",  "CAMILO", "VALENTINA", "SARA"]// objeto tipo string
    }

    buscarAmigoSecreto =()=> {
        this.setState(prevState => {
            return {
            nombre: prevState.listaAmigos.sort(()=> Math.random()- 0.5)
            }
        })
    }
    render(){
        return(
            <div>
                <Hijo nombre={this.state.nombre} buscarAmigo= {this.state.buscarAmigoSecreto.map}/>
            </div>
        )
    }

}

export default Padre;
